-- ----------------------------
-- Table structure for `promo_activity`
-- ----------------------------
DROP TABLE IF EXISTS `promo_activity`;
CREATE TABLE `promo_activity` (
  `activity_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '活动ID',
  `activity_type` int(4) DEFAULT NULL COMMENT '1.普通活动，2.票补活动',
  `channel_type` int(4) DEFAULT NULL COMMENT '1.自营渠道，2.第三方渠道',
  `creator_user` varchar(20) NOT NULL COMMENT '创建活动者',
  `modified_user` varchar(20) DEFAULT NULL COMMENT '修改活动用户',
  `audit_user` varchar(20) DEFAULT NULL COMMENT '审核人',
  `name` varchar(80) NOT NULL COMMENT '活动名称',
  `introduce` varchar(1024) DEFAULT NULL COMMENT '活动介绍，影院内部人员查看',
  `tag` varchar(100) NOT NULL COMMENT '活动标签',
  `supplementary_desc` varchar(100) DEFAULT NULL COMMENT '补充说明',
  `description` text NOT NULL COMMENT '活动说明，营销活动规则详情',
  `biz_type` int(4) NOT NULL COMMENT '营销活动类型\r\n            1：只有票\r\n            2：票+卖品',
  `target_user` int(4) NOT NULL COMMENT '1-全员\r\n            2-持卡用户\r\n            3-非持卡用户',
  `discount_type` int(4) NOT NULL COMMENT '优惠类型\r\n            1：立减\r\n            2：减至',
  `subsidy_mode` int(4) DEFAULT NULL COMMENT '票+卖品策略\\r\\n            0：没有卖品\\r\\n            1：票价卖品平均分摊\\r\\n            2：先票价后卖品\\r\\n            3：先买票后票价',
  `pay_tool` int(4) DEFAULT NULL COMMENT '支付工具\r\n            1：会员卡\r\n            2：支付宝\r\n            3：微信',
  `subsidy_provider` varchar(40) DEFAULT NULL COMMENT '补贴方',
  `price` bigint(20) DEFAULT NULL COMMENT '活动金额，单位为分\\r\\n            当票+卖品时指的是套餐价格\\r\\n            当只有票时指的是减至或立减的金额',
  `extra_price` varchar(128) DEFAULT NULL COMMENT '额外的活动金额，单位为分',
  `budget_id` bigint(20) NOT NULL COMMENT '预算方案ID',
  `min_tickit_count` int(11) DEFAULT NULL,
  `total_limit_count` int(11) DEFAULT NULL COMMENT '总数量限制\r\n            票：票数\r\n            票+卖品：订单数',
  `used_count` int(11) NOT NULL DEFAULT '0' COMMENT '已使用的票/订单数',
  `join_limit_type` int(4) DEFAULT NULL COMMENT '用户参与次数类型\r\n            1：按账户\r\n            2：按会员卡',
  `limit_times` int(11) DEFAULT NULL COMMENT '可参与活动总次数',
  `limit_count_each_time` int(11) DEFAULT NULL COMMENT '票/票+卖品每次可购买数量限制',
  `overlap_mem_card` tinyint(1) DEFAULT NULL COMMENT '是否叠加会员卡优惠',
  `recorded_by_card` tinyint(1) DEFAULT NULL COMMENT '线上活动购买票数是否计入会员卡使用政策可购折扣票数',
  `flags` varchar(32) DEFAULT '00000000000000000000000000000000' COMMENT '标志位 X0X2...X32 X0表示取整策略',
  `activity_status` int(4) NOT NULL COMMENT '活动状态（1：新建，2：待发布，3：执行中，4：暂停，5：结束）',
  `hide` int(4) NOT NULL DEFAULT '0' COMMENT '是否隐藏（0：未隐藏，1：隐藏）',
  `audit_status` int(4) DEFAULT NULL COMMENT '审核状态（1：未提交，2：待审核，3：审核通过，4：审核不通过）',
  `audit_nopass_cause` varchar(1024) DEFAULT NULL COMMENT '审核不通过原因',
  `show_times` varchar(400) DEFAULT NULL COMMENT '活动的排期时间',
  `start_time` datetime NOT NULL,
  `end_time` datetime NOT NULL,
  `channels` varchar(400) DEFAULT NULL COMMENT '渠道，以逗号分隔',
  `extension` varchar(4000) DEFAULT NULL COMMENT '扩展',
  `gmt_created` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `gmt_modified` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '更新时间',
  PRIMARY KEY (`activity_id`)
) ENGINE=InnoDB AUTO_INCREMENT=11140 DEFAULT CHARSET=utf8 COMMENT='活动详情表';

