mapper.insert:
insert into t_attractions (id, prod_name, area_name, 
  adult_price, children_price, dis_price, 
  remark, create_time, update_time
  )
values (#{id,jdbcType=VARCHAR}, #{prodName,jdbcType=VARCHAR}, #{areaName,jdbcType=VARCHAR}, 
  #{adultPrice,jdbcType=VARCHAR}, #{childrenPrice,jdbcType=VARCHAR}, #{disPrice,jdbcType=VARCHAR}, 
  #{remark,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select

from t_attractions where 1=1

  and prod_name like #{prodName,jdbcType=VARCHAR}


  and area_name like #{areaName,jdbcType=VARCHAR}

order by


    adult_price


    children_price


    dis_price


    create_time


${sord}
