mapper.insert:
insert into t_product (id, prod_no,prod_name, start,
  destination, prod_sort, prod_sale,
  create_time, update_time, remark
  )
values (#{id,jdbcType=VARCHAR}, #{prodNo,jdbcType=VARCHAR}, #{prodName,jdbcType=VARCHAR}, #{start,jdbcType=VARCHAR},
  #{destination,jdbcType=VARCHAR}, #{prodSort,jdbcType=VARCHAR}, #{prodSale,jdbcType=VARCHAR},
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:
select

from t_product where 1=1

  and  prod_no like #{prodNo,jdbcType=VARCHAR}


  and prod_name like #{prodName,jdbcType=VARCHAR}


  and start like #{start,jdbcType=VARCHAR}


  and destination like #{destination,jdbcType=VARCHAR}

order by


    prod_no


    prod_sort


    prod_sale


    create_time


 ${sord}
