mapper.insert:
insert into t_travel_line (id, prod_no, spzx, 
  days, jtfs, hotel, 
  remark, create_time, update_time, 
  ext1, ext2, ext3, ext4
  )
values (#{id,jdbcType=VARCHAR}, #{prodNo,jdbcType=VARCHAR}, #{spzx,jdbcType=VARCHAR}, 
  #{days,jdbcType=VARCHAR}, #{jtfs,jdbcType=VARCHAR}, #{hotel,jdbcType=VARCHAR}, 
  #{remark,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{ext1,jdbcType=VARCHAR}, #{ext2,jdbcType=VARCHAR}, #{ext3,jdbcType=VARCHAR}, #{ext4,jdbcType=VARCHAR}
  )

mapper.select:
select

from t_travel_line where 1=1

  and  prod_no like #{prodNo,jdbcType=VARCHAR}


  and hotel like #{hotel,jdbcType=VARCHAR}


  and spzx = #{spzx,jdbcType=VARCHAR}


  and days = #{days,jdbcType=VARCHAR}


  and jtfs like #{jtfs,jdbcType=VARCHAR}

order by


    prod_no


    create_time


${sord}
