mapper.select:
select

from t_travel_zyx where 1=1

  and  pch like #{pch,jdbcType=VARCHAR}


  and  cpid like #{cpid,jdbcType=VARCHAR}


  and  days = #{days,jdbcType=VARCHAR}


  and  jtfs like #{jtfs,jdbcType=VARCHAR}


  and  cfd like #{cfd,jdbcType=VARCHAR}


  and  mdd like #{mdd,jdbcType=VARCHAR}

order by ${sortName} ${sord}
